function scrollAnimation(){if(1!=stop){var n=$(document).scrollTop(),t=$("#index-main-content").find(".content-column");if(!(n>=$(t[0]).offset().top-300))return;for(var e=0;e<t.length;e++)$(t[e]).addClass("animated flipInY"),$(t[e]).css("opacity",1);stop=1}}function handleRecent(){var n=0,t=$(".post-list1").find("li");if(t.length>7)for(n=7;n<t.length;n++)t[n].parentNode.removeChild(t[n]);var e=$(".post-list2").find("li");if(e.length>7)for(n=7;n<e.length;n++)e[n].parentNode.removeChild(e[n])}var stop=0;$(function(){$("#myCarousel").carousel({interval:3e3}),screen.width>425&&$(window).scroll(scrollAnimation),handleRecent()});